import React, { useEffect, useState } from 'react';

const CountryFooter = () => {
  const [countryData, setCountryData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const currentYear = new Date().getFullYear();

  useEffect(() => {
    const fetchCountryData = async () => {
      try {
        setLoading(true);
        const response = await fetch('https://restcountries.com/v3.1/name/India?fullText=true');
        if (!response.ok) throw new Error('Network response was not ok');
        const data = await response.json();
        setCountryData(data[0]); // The API returns an array
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchCountryData();
  }, []);

  return (
    <footer>
      <p>Made with <span style={{ color: 'red' }}>&hearts;</span> by WhisperTales</p>
      <div>
        {loading ? (
          <p>Loading country data...</p>
        ) : error ? (
          <p>Error: {error}</p>
        ) : countryData ? (
          <div>
            <h3>Country Information</h3>
            <p><strong>Country:</strong> {countryData.name.common}</p>
            <p><strong>Capital:</strong> {countryData.capital ? countryData.capital[0] : 'N/A'}</p>
            <p><strong>Region:</strong> {countryData.region}</p>
            <p><strong>Population:</strong> {countryData.population.toLocaleString()}</p>
            <p><strong>Area:</strong> {countryData.area.toLocaleString()} kmÂ²</p>
            <p><strong>Currency:</strong> {Object.values(countryData.currencies || {}).map(c => c.name).join(', ')}</p>
          </div>
        ) : (
          <p>No data available.</p>
        )}
      </div>
    </footer>
  );
};

export default CountryFooter;
