import { useState, useEffect } from "react";
import { Navigate } from "react-router-dom";
import Editor from "../Components/Editor";
import ConfettiExplosion from 'react-confetti-explosion';

// Use environment variable for API base URL
const API_BASE_URL = process.env.REACT_APP_API_BASE_URL;

export default function CreatePost() {
    const [title, setTitle] = useState('');
    const [summary, setSummary] = useState('');
    const [content, setContent] = useState('');
    const [file, setFile] = useState('');
    const [redirect, setRedirect] = useState(false);
    const [showConfetti, setShowConfetti] = useState(false);

    async function createNewPost(e) {
        e.preventDefault();
        const postData = {
            title,
            summary,
            content,
            file
        };

        try {
            const response = await fetch(`${API_BASE_URL}/post`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(postData),
                credentials: 'include',
            });

            if (response.ok) {
                setShowConfetti(true); // Trigger confetti
                setTimeout(() => setRedirect(true), 3000); // Redirect after confetti animation
            } else {
                console.error('Failed to create post');
            }
        } catch (error) {
            console.error('Error creating post:', error);
        }
    }

    if (redirect) {
        return <Navigate to="/" />;
    }

    return (
        <div>
            {showConfetti && (
                <ConfettiExplosion
                    force={0.8} // Adjust force of explosion
                    duration={3000} // Duration of explosion
                    particleCount={200} // Number of particles
                    colors={['#ff0000', '#00ff00', '#0000ff']} // Customize colors
                />
            )}
            <form onSubmit={createNewPost}>
                <input
                    type="text"
                    placeholder="Title"
                    value={title}
                    onChange={e => setTitle(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Summary"
                    value={summary}
                    onChange={e => setSummary(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Image Link"
                    value={file}
                    onChange={e => setFile(e.target.value)}
                />
                <Editor
                    onChange={setContent}
                    value={content}
                />
                <button type="submit">Create Post</button>
            </form>
        </div>
    );
}
